INSERT INTO `wp_postmeta` VALUES (2412,1821,'modules_1_blurb_1_title','Blueberries');
INSERT INTO `wp_postmeta` VALUES (2413,1821,'_modules_1_blurb_1_title','field_5ad4deb0d4909');
INSERT INTO `wp_postmeta` VALUES (2414,1821,'modules_1_blurb_1_content','Pick Your Own Blueberries typically begins mid July and continues through August. Delicate, delicious, and great for snacking; blueberries are one of the most nutritional berries you can eat – they are low in fat and calories, high in fiber and Vitamin C. and are rich in antioxidants. When picking, be sure to look for berries that are very dark blue in color – green berries have not ripened fully and are not ready to pick. Visit us again for updated 2018 PYO pricing as we approach the harvest time and are better able to evaluate this year’s crop.');
INSERT INTO `wp_postmeta` VALUES (2415,1821,'_modules_1_blurb_1_content','field_5ad4deb6d490a');
