INSERT INTO `wp_postmeta` VALUES (2418,1821,'modules_1_blurb_2_title','Peaches');
INSERT INTO `wp_postmeta` VALUES (2419,1821,'_modules_1_blurb_2_title','field_5ad4deb0d4909');
INSERT INTO `wp_postmeta` VALUES (2420,1821,'modules_1_blurb_2_content','Pick Your Own Peaches typically begins in early-August and continues through mid-September. A short walk around the pond and up to the Hill Top Orchard, peach picking requires a bit of a hike but is well worth the effort after sinking your teeth into the fuzzy-skin that is sweetly juicy with each bite. Peaches are a great source of Vitamin C and dietary fiber among other small quantities of vitamins and minerals including potassium. Enjoy all the health benefits and savor this luscious fruit fresh or prepared as a dessert and/or addition to your summer BBQ dinner – they can even be grilled! When picking, be sure to look for fruit that are dark yellow/red in color – green/white fruit have not ripened fully and are not ready to pick. Visit us again for updated 2018 PYO pricing as we approach the harvest time and are better able to evaluate this year’s crop.');
INSERT INTO `wp_postmeta` VALUES (2421,1821,'_modules_1_blurb_2_content','field_5ad4deb6d490a');
