INSERT INTO `wp_postmeta` VALUES (2437,1822,'modules_0_blurb_0_content','<p class=\"p1\">March Farm is a fourth generation family farm destination offering seasonal Pick Your Own berries &amp; fruit along with family friendly activities. The Farm Store &amp; Bakery is stocked with farm grown produce, regional food specialties and fresh baked goods – everything you need to compliment your next meal or dessert. The rustically beautiful Pond Side Pines &amp; Hill Top Orchard event spaces are perfect to host your next special occasion, outing or party no matter how small or large.<span class=\"Apple-converted-space\">  </span>Join us for our next Farm To Table Dinner and other special events – there’s always something to do at March Farm!</p>');
