INSERT INTO `wp_postmeta` VALUES (2637,1825,'modules_1_rightimgblurb_0_content','<p class=\"p1\">Celebrate your next special occasion in our rustically beautiful event space surrounded by majestic pines by the water. This uniquely versatile space features the perfect amenities to host a variety of events including:</p>\r\n\r\n<ul>\r\n 	<li><span style=\"font-weight: 400;\">Elegant Country Weddings</span></li>\r\n 	<li><span style=\"font-weight: 400;\">Exciting Outings &amp; Retreats</span></li>\r\n 	<li><span style=\"font-weight: 400;\">Unforgettable Family Gatherings</span></li>\r\n</ul>\r\n<span style=\"font-weight: 400;\">For a complete list of amenities, activities &amp; to schedule a walk through please email our Event Planner today.</span>\r\n\r\n&nbsp;\r\n\r\n&nbsp;');
