INSERT INTO `wp_postmeta` VALUES (2665,1825,'modules_3_rightimgblurb_0_content','<span style=\"font-weight: 400;\">Breathtaking views of the beautiful rolling hills of Litchfield County highlight the Hill Top Orchard event space, perfect for hosting:</span>\r\n<ul>\r\n 	<li><span style=\"font-weight: 400;\">Large Country Weddings</span></li>\r\n 	<li><span style=\"font-weight: 400;\">Farm To Table Dinners</span></li>\r\n 	<li><span style=\"font-weight: 400;\">Charity &amp; Fundraising Events</span></li>\r\n 	<li><span style=\"font-weight: 400;\">Reunions</span></li>\r\n</ul>\r\n<span style=\"font-weight: 400;\">To plan your next event on the Hill Top, please email our Event Coordinator today!</span>');
