INSERT INTO `wp_postmeta` VALUES (2674,1825,'_modules_4_leftimgblurb_0_title','field_5ad4e88cf1730');
INSERT INTO `wp_postmeta` VALUES (2675,1825,'modules_4_leftimgblurb_0_content','<span style=\"font-weight: 400;\">Treat your students to a day on the farm with a field trip to March Farm.  Available weekdays in September &amp; October, field trips to March Farm are a fun way for your students to explore an operational orchard, enormous pumpkin patch, corn maze, and the Hayloft Playscape &amp; Animal Yard.  Depending on the activities you choose to include typical field trips last 2-3 hours and follow this general schedule:</span>\r\n\r\n<span style=\"font-weight: 400;\">10AM:  Arrival and direction to picking areas.  If the corn maze is included in your package that would be the first activity before picking so students don’t have to carry apples/pumpkins all the way through.</span>\r\n\r\n<span style=\"font-weight: 400;\">11AM:  Playtime at the Hayloft Playscape &amp; Animal Yard.</span>\r\n\r\n<span style=\"font-weight: 400;\">12PM:  Lunch under the pavilion at the Hayloft Playscape.</span>\r\n\r\n<span style=\"font-weight: 400;\">1PM:  Departure</span>\r\n\r\n<span style=\"font-weight: 400;\">Please email our field trip coordinator to plan your field trip today!</span>');
