INSERT INTO `wp_postmeta` VALUES (2805,2189,'modules_1_accordian_tab_0_headline','Is there an admission fee?');
INSERT INTO `wp_postmeta` VALUES (2806,2189,'_modules_1_accordian_tab_0_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2807,2189,'modules_1_accordian_tab_0_content','<span style=\"font-weight: 400;\">No, the farm is free to visit but charges apply when shopping at the Farm Store, picking berries or fruit, exploring the Hill Top Corn Maze or taking a hayride.  The Hayloft Playscape &amp; Animal Yard are free of charge.</span>');
INSERT INTO `wp_postmeta` VALUES (2808,2189,'_modules_1_accordian_tab_0_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2809,2189,'modules_1_accordian_tab_1_headline','Are dogs (or other pets) allowed at March Farm?');
INSERT INTO `wp_postmeta` VALUES (2810,2189,'_modules_1_accordian_tab_1_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2811,2189,'modules_1_accordian_tab_1_content','<span style=\"font-weight: 400;\">No – with the exception of service dogs.</span>');
INSERT INTO `wp_postmeta` VALUES (2812,2189,'_modules_1_accordian_tab_1_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2813,2189,'modules_1_accordian_tab_2_headline','What kind of daily activities are available?');
INSERT INTO `wp_postmeta` VALUES (2814,2189,'_modules_1_accordian_tab_2_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2815,2189,'modules_1_accordian_tab_2_content','<span style=\"font-weight: 400;\">The Farm Store &amp; Bakery is open daily from 10am to 6pm along with the Hayloft Playscape &amp; Animal Yard.  Seasonal Pick Your Own cherries, blueberries, peaches, apples &amp; pumpkins are also available through the summer and fall.  Pricing for Pick Your Own items is seasonally based and can be found on their respective pages.</span>');
INSERT INTO `wp_postmeta` VALUES (2816,2189,'_modules_1_accordian_tab_2_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2817,2189,'modules_1_accordian_tab_3_headline','Is March Farm open year round?');
INSERT INTO `wp_postmeta` VALUES (2818,2189,'_modules_1_accordian_tab_3_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2819,2189,'modules_1_accordian_tab_3_content','<span style=\"font-weight: 400;\">No – we are open May through December.</span>');
INSERT INTO `wp_postmeta` VALUES (2820,2189,'_modules_1_accordian_tab_3_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2821,2189,'modules_1_accordian_tab_4_headline','Do you offer a CSA (Community Supported Agriculture) program?');
INSERT INTO `wp_postmeta` VALUES (2822,2189,'_modules_1_accordian_tab_4_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2823,2189,'modules_1_accordian_tab_4_content','<span style=\"font-weight: 400;\">Yes – please visit our </span><b><i>CSA </i></b><span style=\"font-weight: 400;\">pages for additional information.</span>');
INSERT INTO `wp_postmeta` VALUES (2824,2189,'_modules_1_accordian_tab_4_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2825,2189,'modules_1_accordian_tab_5_headline','When do seasonal activities and pick your own offerings begin & end?');
INSERT INTO `wp_postmeta` VALUES (2826,2189,'_modules_1_accordian_tab_5_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2827,2189,'modules_1_accordian_tab_5_content','<span style=\"font-weight: 400;\">Please reference our </span><b><i>Events Calendar</i></b><span style=\"font-weight: 400;\">.</span>');
INSERT INTO `wp_postmeta` VALUES (2828,2189,'_modules_1_accordian_tab_5_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2829,2189,'modules_1_accordian_tab_6_headline','What type of events do you host and are open to the public?');
INSERT INTO `wp_postmeta` VALUES (2830,2189,'_modules_1_accordian_tab_6_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2831,2189,'modules_1_accordian_tab_6_content','<span style=\"font-weight: 400;\">Seasonal events that are open to the public include Farm To Table Dinners, Harvest Festival, Cyclocross Bike Race, Wreath Making Workshops and a visit by Santa Claus are all special events hosted by the farm and open to the public.</span>');
INSERT INTO `wp_postmeta` VALUES (2832,2189,'_modules_1_accordian_tab_6_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2833,2189,'modules_1_accordian_tab_7_headline','Are your baked goods made on the farm?');
INSERT INTO `wp_postmeta` VALUES (2834,2189,'_modules_1_accordian_tab_7_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2835,2189,'modules_1_accordian_tab_7_content','<span style=\"font-weight: 400;\">Yes – our baked goods are daily made from scratch in the farm bakery.</span>');
INSERT INTO `wp_postmeta` VALUES (2836,2189,'_modules_1_accordian_tab_7_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2837,2189,'modules_1_accordian_tab_8_headline','Are produce items grown on the farm Non-GMO?');
INSERT INTO `wp_postmeta` VALUES (2838,2189,'_modules_1_accordian_tab_8_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2839,2189,'modules_1_accordian_tab_8_content','<span style=\"font-weight: 400;\">Yes – we do not use any GMO seeds.</span>');
INSERT INTO `wp_postmeta` VALUES (2840,2189,'_modules_1_accordian_tab_8_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2841,2189,'modules_1_accordian_tab_9_headline','Is March Farm an organic farm?');
INSERT INTO `wp_postmeta` VALUES (2842,2189,'_modules_1_accordian_tab_9_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2843,2189,'modules_1_accordian_tab_9_content','<span style=\"font-weight: 400;\">No, but we do grow a number of our crops chemically free without the use of pesticides or fungicides in accordance to a strict Integrated Pest Management Program.  Visit our </span><b><i>Growing Practices</i></b><span style=\"font-weight: 400;\"> page to learn more</span>');
INSERT INTO `wp_postmeta` VALUES (2844,2189,'_modules_1_accordian_tab_9_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2845,2189,'modules_1_accordian_tab_10_headline','Does March Farm apply pesticides/fungicides to crops?');
INSERT INTO `wp_postmeta` VALUES (2846,2189,'_modules_1_accordian_tab_10_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2847,2189,'modules_1_accordian_tab_10_content','<span style=\"font-weight: 400;\">Yes, but not to all of our crops and only when certain pest/disease thresholds have been exceeded in accordance to a strict Integrated Pest Management Program.  Visit our </span><b><i>Growing Practices</i></b><span style=\"font-weight: 400;\"> page to learn more.</span>');
INSERT INTO `wp_postmeta` VALUES (2848,2189,'_modules_1_accordian_tab_10_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2849,2189,'modules_1_accordian_tab_11_headline','What apple varieties do we grow?');
INSERT INTO `wp_postmeta` VALUES (2850,2189,'_modules_1_accordian_tab_11_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2851,2189,'modules_1_accordian_tab_11_content','<span style=\"font-weight: 400;\">Visit our Pick Your Own Apples page for a complete list of the apple varieties that we grow.</span>');
INSERT INTO `wp_postmeta` VALUES (2852,2189,'_modules_1_accordian_tab_11_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2853,2189,'modules_1_accordian_tab_12_headline','How many acres is March Farm and what is the history behind it?');
INSERT INTO `wp_postmeta` VALUES (2854,2189,'_modules_1_accordian_tab_12_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2855,2189,'modules_1_accordian_tab_12_content','<span style=\"font-weight: 400;\">March Farm is a 4</span><span style=\"font-weight: 400;\">th</span><span style=\"font-weight: 400;\"> generation family owned and operated farm that is 150 acres and over 100 years old.  Visit our </span><b><i>History</i></b><span style=\"font-weight: 400;\"> page for additional information.</span>');
INSERT INTO `wp_postmeta` VALUES (2856,2189,'_modules_1_accordian_tab_12_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2857,2189,'modules_1_accordian_tab_13_headline','Does March Farm host weddings and other special events?');
INSERT INTO `wp_postmeta` VALUES (2858,2189,'_modules_1_accordian_tab_13_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2859,2189,'modules_1_accordian_tab_13_content','<span style=\"font-weight: 400;\">Yes – we host weddings, corporate retreats, family gatherings, birthday parties and other special events.  Visit our </span><b><i>Weddings, Outings &amp; Events and Hayloft Playscape</i></b><span style=\"font-weight: 400;\"> pages for additional information.</span>');
INSERT INTO `wp_postmeta` VALUES (2860,2189,'_modules_1_accordian_tab_13_content','field_5ad63dc0977c6');
