INSERT INTO `wp_postmeta` VALUES (2871,2189,'modules_3_accordian_tab_0_headline','When does the Hill Top Corn Maze open?');
INSERT INTO `wp_postmeta` VALUES (2872,2189,'_modules_3_accordian_tab_0_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2873,2189,'modules_3_accordian_tab_0_content','<span style=\"font-weight: 400;\">The corn maze opens weekends in September &amp; October starting September 15</span><span style=\"font-weight: 400;\">th</span><span style=\"font-weight: 400;\"> from 10AM to 5PM .</span>');
INSERT INTO `wp_postmeta` VALUES (2874,2189,'_modules_3_accordian_tab_0_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2875,2189,'modules_3_accordian_tab_1_headline','How big is the maze?');
INSERT INTO `wp_postmeta` VALUES (2876,2189,'_modules_3_accordian_tab_1_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2877,2189,'modules_3_accordian_tab_1_content','<span style=\"font-weight: 400;\">The corn covers over 5 acres of ground and contains over 2 miles of pathways guaranteed to get you and your family or friends lost.</span>');
INSERT INTO `wp_postmeta` VALUES (2878,2189,'_modules_3_accordian_tab_1_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2879,2189,'modules_3_accordian_tab_2_headline','How long does it take to go through the maze?');
INSERT INTO `wp_postmeta` VALUES (2880,2189,'_modules_3_accordian_tab_2_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2881,2189,'modules_3_accordian_tab_2_content','<span style=\"font-weight: 400;\">It all depends on how many wrong turns you take. The average person will finish the corn maze within ½ hour. Some people have taken a lot longer and a few a bit quicker. </span>');
INSERT INTO `wp_postmeta` VALUES (2882,2189,'_modules_3_accordian_tab_2_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2883,2189,'modules_3_accordian_tab_3_headline','Will I get Lost?');
INSERT INTO `wp_postmeta` VALUES (2884,2189,'_modules_3_accordian_tab_3_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2885,2189,'modules_3_accordian_tab_3_content','<span style=\"font-weight: 400;\">We hope so! It’s designed to be that way. While everyone does get lost to a different degree, we have designed the maze to be an interactive activity that makes a trip inside the maze more than just a walk through some corn. At the beginning of your visit you read a banner describing that year’s maze theme. There are then 7 to 8 points in the maze where a question is asked about what you read at the start with 3 different ways to go.  If you choose the wrong way, you will eventually be looped back to the question point to try again.</span>');
INSERT INTO `wp_postmeta` VALUES (2886,2189,'_modules_3_accordian_tab_3_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2887,2189,'modules_3_accordian_tab_4_headline','Is there an admission fee?');
INSERT INTO `wp_postmeta` VALUES (2888,2189,'_modules_3_accordian_tab_4_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2889,2189,'modules_3_accordian_tab_4_content','<span style=\"font-weight: 400;\">Yes - $8/person (children 2 years old and under are free).</span>');
INSERT INTO `wp_postmeta` VALUES (2890,2189,'_modules_3_accordian_tab_4_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2891,2189,'modules_3_accordian_tab_5_headline','What forms of payment do you accept?');
INSERT INTO `wp_postmeta` VALUES (2892,2189,'_modules_3_accordian_tab_5_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2893,2189,'modules_3_accordian_tab_5_content','<span style=\"font-weight: 400;\">The preferred method of payment at the maze is cash (US currency only, please), but we also accept most debit/credit cards. </span>');
INSERT INTO `wp_postmeta` VALUES (2894,2189,'_modules_3_accordian_tab_5_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2895,2189,'modules_3_accordian_tab_6_headline','What age is the Hill Top Corn Maze most appropriate for?');
INSERT INTO `wp_postmeta` VALUES (2896,2189,'_modules_3_accordian_tab_6_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2897,2189,'modules_3_accordian_tab_6_content','<span style=\"font-weight: 400;\">One of the great things about Hill Top Corn Maze is the fact that it offers something that everyone of all ages can enjoy. From babies all the way up to grandma and grandpa, our goal is to make sure everyone in your group leaves with memories to cherish.</span>');
INSERT INTO `wp_postmeta` VALUES (2898,2189,'_modules_3_accordian_tab_6_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2899,2189,'modules_3_accordian_tab_7_headline','Is there a haunted version of the corn maze?');
INSERT INTO `wp_postmeta` VALUES (2900,2189,'_modules_3_accordian_tab_7_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2901,2189,'modules_3_accordian_tab_7_content','<span style=\"font-weight: 400;\">Although there is no haunted version of the corn maze, we do offer a Haunted Pumpkin Patch in October.</span>');
INSERT INTO `wp_postmeta` VALUES (2902,2189,'_modules_3_accordian_tab_7_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2903,2189,'modules_3_accordian_tab_8_headline','Are you open during bad/inclement weather?');
INSERT INTO `wp_postmeta` VALUES (2904,2189,'_modules_3_accordian_tab_8_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2905,2189,'modules_3_accordian_tab_8_content','<span style=\"font-weight: 400;\">Unforeseen weather can force us to close at any time, an announcement regarding any weather closures will be communicated to our staff who will in turn communicate to the public.  Our web site and Facebook page will also be updated accordingly.</span>');
INSERT INTO `wp_postmeta` VALUES (2906,2189,'_modules_3_accordian_tab_8_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2907,2189,'modules_3_accordian_tab_9_headline','Are there refunds if guests leave due to weather?');
INSERT INTO `wp_postmeta` VALUES (2908,2189,'_modules_3_accordian_tab_9_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2909,2189,'modules_3_accordian_tab_9_content','<span style=\"font-weight: 400;\">No, but rainchecks will be issued allowing you to return on a future date.</span>');
INSERT INTO `wp_postmeta` VALUES (2910,2189,'_modules_3_accordian_tab_9_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2911,2189,'modules_3_accordian_tab_10_headline','How accessible is the maze?');
INSERT INTO `wp_postmeta` VALUES (2912,2189,'_modules_3_accordian_tab_10_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2913,2189,'modules_3_accordian_tab_10_content','<span style=\"font-weight: 400;\">Fritzler Maze is handicap accessible. The corn maze and courtyard itself however are on unpaved farm ground. Wheel chairs, strollers and wagons can be brought on premises and are able to complete the corn maze, but the ground (like any farm) can be bumpy or in some cases muddy in spots.</span>');
INSERT INTO `wp_postmeta` VALUES (2914,2189,'_modules_3_accordian_tab_10_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2915,2189,'modules_3_accordian_tab_11_headline','Are there restrooms on site?');
INSERT INTO `wp_postmeta` VALUES (2916,2189,'_modules_3_accordian_tab_11_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2917,2189,'modules_3_accordian_tab_11_content','<span style=\"font-weight: 400;\">There are port-o-let units on site.</span>');
INSERT INTO `wp_postmeta` VALUES (2918,2189,'_modules_3_accordian_tab_11_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2919,2189,'modules_3_accordian_tab_12_headline','What should we wear?');
INSERT INTO `wp_postmeta` VALUES (2920,2189,'_modules_3_accordian_tab_12_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2921,2189,'modules_3_accordian_tab_12_content','<span style=\"font-weight: 400;\">Because we operate in September and October, we recommend being prepared for cool weather. We also recommend wearing closed-toe shoes or sneakers. Remember that you will be in a farm atmosphere and should dress appropriately for the best experience.</span>');
INSERT INTO `wp_postmeta` VALUES (2922,2189,'_modules_3_accordian_tab_12_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2923,2189,'modules_3_accordian_tab_13_headline','When is the best time to come to the maze?');
INSERT INTO `wp_postmeta` VALUES (2924,2189,'_modules_3_accordian_tab_13_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2925,2189,'modules_3_accordian_tab_13_content','<span style=\"font-weight: 400;\">The corn maze opens weekends in September &amp; October starting September 15</span><span style=\"font-weight: 400;\">th</span><span style=\"font-weight: 400;\"> from 10AM to 5PM.</span>');
INSERT INTO `wp_postmeta` VALUES (2926,2189,'_modules_3_accordian_tab_13_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2927,2189,'modules_3_accordian_tab_14_headline','Are we allowed to bring our dog with us to the maze?');
INSERT INTO `wp_postmeta` VALUES (2928,2189,'_modules_3_accordian_tab_14_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2929,2189,'modules_3_accordian_tab_14_content','<span style=\"font-weight: 400;\">Unfortunately there are no pets allowed in the corn maze (or Farm in general) with the exception of service animals.</span>');
INSERT INTO `wp_postmeta` VALUES (2930,2189,'_modules_3_accordian_tab_14_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2931,2189,'modules_3_accordian_tab_15_headline','What are the maze rules?');
INSERT INTO `wp_postmeta` VALUES (2932,2189,'_modules_3_accordian_tab_15_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2933,2189,'modules_3_accordian_tab_15_content','<span style=\"font-weight: 400;\">No running; no smoking; no alcohol; no throwing corn (kernels, ears, or stalks); stay on the designated pathways; please refrain from using obscene/derogatory comments, gestures, etc. while at the maze.</span>');
INSERT INTO `wp_postmeta` VALUES (2934,2189,'_modules_3_accordian_tab_15_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2935,2189,'modules_3_accordian_tab_16_headline','How do you make the maze?');
INSERT INTO `wp_postmeta` VALUES (2936,2189,'_modules_3_accordian_tab_16_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2937,2189,'modules_3_accordian_tab_16_content','<span style=\"font-weight: 400;\">Our particular process of making the maze is a trade secret, but we can tell you that we plant the whole field in the spring and pull the pathways by hand when the corn is about 1’ high. The design is created as the corn is pulled without the aid of computers and/or GPS systems.</span>');
INSERT INTO `wp_postmeta` VALUES (2938,2189,'_modules_3_accordian_tab_16_content','field_5ad63dc0977c6');
INSERT INTO `wp_postmeta` VALUES (2939,2189,'modules_3_accordian_tab_17_headline','Can I eat the corn?');
INSERT INTO `wp_postmeta` VALUES (2940,2189,'_modules_3_accordian_tab_17_headline','field_5ad63db5977c5');
INSERT INTO `wp_postmeta` VALUES (2941,2189,'modules_3_accordian_tab_17_content','<span style=\"font-weight: 400;\">You could, but it wouldn’t taste very good. The variety of corn that we grow for the maze is field corn for cattle.</span>');
INSERT INTO `wp_postmeta` VALUES (2942,2189,'_modules_3_accordian_tab_17_content','field_5ad63dc0977c6');
